components/common/pagination:
{{#if links.length '>' 1}}
  <div class="pagination-container">
    {{#if previous}}
      <div class="pagination-link pagination-previous">
        <a class="previous" href="{{previous}}" rel="nofollow" data-faceted-search-facet>{{lang 'core.pagination.previous'}}</a>
      </div>
    {{/if}}

    {{#each links}}
      <div class="pagination-number{{#if ../current '===' number}} current{{/if}}">
        <a href="{{url}}" rel="nofollow" data-faceted-search-facet data-pagination-current-page-link >{{number}}</a>
      </div>
    {{/each}}

    {{#if next}}
      <div class="pagination-link pagination-next">
        <a class="next" href="{{next}}" rel="nofollow" data-faceted-search-facet>{{lang 'core.pagination.next'}}</a>
      </div>
    {{/if}}
  </div>
{{/if}}
